mapper.update:
 		UPDATE Community
		SET commuComNum = #{commuComNum}
		WHERE commuNo=#{commuNo}

UPDATE Community
      SET commuView = commuView + 1
 WHERE commuNo=#{commuNo}

UPDATE Community
SET commuTitle='${commuTitle}', commuInfo='${commuInfo}', commuCtgNo = '${commuCtgNo}'
WHERE commuNo=#{commuNo}

UPDATE Community
      SET commuComNum = commuComNum - 1
 WHERE commuNo=#{commuNo}
